
public class Demo2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 9;
		int b = 5;
		
		System.out.println("a = " + a + ", b = " + b);
		System.out.println(a + " + " + b + " = " + (a+b));
		System.out.println(a + " - " + b + " = " + (a-b));
		System.out.println(a + " * " + b + " = " + (a*b));
		System.out.println(a + " / " + b + " = " + (a/b));
		System.out.println(a + " % " + b + " = " + (a%b));
	}

}
